import React, { useState } from "react";


export default function TextForm(props) {
  const handleUpClick = () => {
    console.log("UpperCase Was Clicked" + text);
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleLoClick = () => {
    console.log("LowerCase Was Clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };

  const handleOnChange = (event) => {
    console.log("On change");
    setText(event.target.value);
  };

  const handleItClick = () => {
    console.log("Italic was Pressed");
    setStyle({ fontStyle: "italic" });
  };

  const handleBclick = () =>
  {
    console.log("Bold was Pressed");
    setStyle({fontWeight: "bold"});

  }
  const handleClear = () =>
  {
    console.log("Clear Clicked");
    setStyle({fontWeight: style.fontWeight === "bold" ? "normal" : "bold" }, {fontStyle: style.fontStyle === "italic" ? "normal" : "italic"});
    setText("");
  }

  const [text, setText] = useState("Enter Text Here");
  const [style, setStyle] = useState({ fontStyle: "normal" });

  return (
    <>
      <div className="Container" style={{color: props.mode==='dark'?'white':'black'}}>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <label for="myBox" className="form-label"></label>
          <textarea className="form-control" value={text} onChange={handleOnChange} style={{backgroundColor: props.mode==='dark'?'grey':'white'}} id="myBox" rows="8"></textarea>
        </div>
        <button className="btn btn-dark mx-3" onClick={handleUpClick}>UpperCase</button>
        <button className="btn btn-dark" onClick={handleLoClick}>LowerCase</button>
        <button className="btn btn-dark mx-3" onClick={handleItClick}>Italic</button>
        <button className="btn btn-dark mx-1" onClick={handleBclick}>Bold</button>
        <button className="btn btn-dark mx-2" onClick={handleClear}>Clear</button>
      </div>

      <div className="container my-4">
        <h2>Text Summary</h2>
        <p>{text.split(" ").length} words & {text.length} Characters
</p>
        <p1> {0.008 * text.split(" ").length} Minutes Read.</p1>
      </div>
    </>
  );
}
