import React, { useState } from "react";
import "./textform.css";

export default function TextForm(props) {
  const [text, setText] = useState("Enter Text Here");
  const [originalText, setOriginalText] = useState("");
  const [style, setStyle] = useState({ fontStyle: "normal" });

  const handleUpClick = () => {
    console.log("UpperCase Was Clicked" + text);
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleLoClick = () => {
    console.log("LowerCase Was Clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };

  const handleOnChange = (event) => {
    console.log("On change");
    setOriginalText(text); // Save the original text
    setText(event.target.value);
  };

  const handleUndoClick = () => {
    console.log("Undo Was Clicked");
    setText(originalText); // Restore the original text
  };

  const handleItClick = () => {
    console.log("Italic was Pressed");
    setStyle({ fontStyle: "italic" });
  };

  const handleBclick = () => {
    console.log("Bold was Pressed");
    setStyle({ fontWeight: "bold" });
  };

  return (
    <>
      <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <label for="myBox" className="form-label"></label>
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
            style={style}
          ></textarea>
        </div>
        <button
          className="btn btn-primary mx-3 my-btn-blue"
          onClick={handleUpClick}
        >
          Convert to UpperCase
        </button>
        <button
          className="btn btn-primary my-btn-blue"
          onClick={handleLoClick}
        >
          Change to LowerCase
        </button>
        <button
          className="btn btn-primary mx-3 my-btn-blue"
          onClick={handleItClick}
        >
          Italic
        </button>
        <button
          className="btn btn-primary mx-1 my-btn-blue"
          onClick={handleBclick}
        >
          Bold
        </button>
        <button
          className="btn btn-primary mx-3 my-btn-blue"
          onClick={handleUndoClick}
          disabled={!originalText} // Disable the button if there is no original text
        >
          Undo
        </button>
      </div>

      <div className="container my-4">
        <h2>Text Summary</h2>
        <p>
          {text.split(" ").length} words & {text.length} Characters
        </p>
        <p1> {0.008 * text.split(" ").length} Minutes Read.</p1>
      </div>
    </>
  );
}
