import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    console.log("UpperCase Was Clicked" + text);
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleLoClick = () => {
    console.log("LowerCase Was Clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };

  const handleOnChange = (event) => {
    console.log("On change");
    setText(event.target.value);
  };

  const handleItClick = () => {
    console.log("To Italic was Pressed");
    setStyle({ fontStyle: "italic" });
  };

  const [text, setText] = useState("Enter Text Here");
  const [style, setStyle] = useState({ fontStyle: "normal" });

  return (
    <>
      <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <label for="myBox" className="form-label"></label>
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
            style={style} // add style attribute here
          ></textarea>
        </div>
        <button className="btn btn-primary mx-3" onClick={handleUpClick}>
          Convert to UpperCase
        </button>
        <button className="btn btn-primary" onClick={handleLoClick}>
          Change to LowerCase
        </button>
        <button className="btn btn-primary" mx-3 onClick={handleItClick}>
          Change to Italic
        </button>
      </div>

      <div className="container my-4">
        <h2>Text Summary</h2>
        <p>
          {text.split(" ").length} words & {text.length} Characters
        </p>
        <p1> {0.008 * text.split(" ").length} Minutes Read.</p1>
      </div>
    </>
  );
}
